import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { transition1 } from '../transitions';
import { CursorContext } from '../context/CursorContext';
import Man1 from '../assets/about/man1.png';

const About = () => {
  return (
    <section className='section'>
      <div className='container mx-auto h-full relative'>
        <div className='bg-pink-200 flex flex-col lg:flex-row h-full items-center justify-center gap-x-24 text-center lg:text-left lg:pt-16'>
          <div className='flex-1 max-h-96 lg:max-h-max order-2 lg:order-none overflow-hidden'>
            <img src={Man1} alt='Man1' className='max-w-full' /> {/* Image Section */}
          </div>
          <div>
            <h2 className='h1'>About Me</h2>
            <p className='mb-12 max-w-sm'>
            Hello! I'm Martin, a passionate nature and landscape photographer captivated by the beauty and tranquility of the natural world. With a childhood rooted in wilderness exploration, my journey into photography has evolved into a profound desire to encapsulate nature's wonders in every frame. Through my lens, I strive to convey not just visual splendor, but also the emotions and narratives woven into the landscape. With a keen eye for composition and a commitment to authenticity, I traverse diverse terrains, from rugged mountain peaks to serene coastal shores, seeking that perfect shot. Each click of the shutter freezes a moment in time, inviting viewers to immerse themselves in the beauty of our planet and perhaps discover a deeper connection with it.</p>
					<br />
					<p className='mb-12 max-w-sm'>Beyond photography, I advocate for environmental conservation, aiming to raise awareness about the fragile beauty of our planet and the urgent need to protect and preserve it for generations to come. Join me on this journey through the lens, where every image tells a story, and every landscape whispers secrets of the earth. Together, let's celebrate the magnificence of nature and revel in its timeless allure.</p>
            <Link to={'/portfolio'} className='btn'>See my Works</Link> 
          </div>
        </div>
      </div>      
    </section>
  );
};

export default About;
