import React from 'react';
import hero from '../assets/home/photoguy.png';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <section className='section'>
      <div className='container mx-auto pt-24 lg:flex lg:justify-between lg:items-center'> {/* Added pt-24 and flexbox classes */}
        <div className='lg:w-1/2 lg:pr-12'> {/* Adjusted width and padding */}
          <div className='flex flex-col justify-center items-center lg:items-start'>
            <h1 className='h1'>
              Nature<br />& <br />Landscape <br />Photographer
            </h1>
            <p className='text-lg lg:text-xl font-primary mb-4 lg:mb-12'>San Francisco, CA & Dallas, TX</p>
            <Link to='/contact' className='btn mb-4 lg:mb-0'>Let's Connect</Link>
          </div>
        </div>
        <div className='lg:w-1/2 mt-8 lg:mt-0'> {/* Added mt-8 lg:mt-0 */}
          <img src={hero} alt='photographer' className='max-w-full h-auto' /> {/* Ensured the image fits the container width */}
        </div>
      </div>
    </section>
  );
};

export default Home;
